package condition_and_loops_calculator;

public class conditionif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int salary = 250000;
		if(salary > 10000) {
			salary = salary +10000;
		}else{
			salary = salary +5000;
			
		}
		System.out.println("salary is :"+ salary);
	}

}
